function App() {

 const [notearray, setNoteArray] = React.useState([]);

 function addNote(newNote) {
   setNoteArray(prevNotes => {
     return [...prevNotes, newNote];
   });
 }

  return (
    <div>
      <Header />
      <CreateArea 
        onAdd={addNote(newnote)} 
      />
      <Note key={1} title="Note title" content="Note content" />
      <Footer />
    </div>
  );
}

export default App;
