declare module "material" {
  // Base classes
  export class Component {
    constructor(options?: any);
    options: any;
    element: HTMLElement;
    ui: any;
    underlay?: HTMLElement;
    position(target: HTMLElement): void;
    destroy(): Promise<void>;
    static defaults: any;
  }

  export class Control extends Component {}

  export class Element {
    constructor(options?: any);
    static isElement(): boolean;
  }

  // Components
  export class AppBar extends Component {}
  export class Badge extends Component {}
  export class Button extends Component {}
  export class Card extends Component {}
  export class Checkbox extends Component {}
  export class ColorPicker extends Component {}
  export class DateHour extends Component {}
  export class Dialog extends Component {}
  export class Drawer extends Component {}
  export class File extends Component {}
  export class Form extends Component {}
  export class Image extends Component {}
  export class List extends Component {}
  export class Loading extends Component {}
  export class Menu extends Component {}
  export class Navigation extends Component {}
  export class Progress extends Component {}
  export class Select extends Component {}
  export class Selecter extends Component {}
  export class Snackbar extends Component {}
  export class Slider extends Component {}
  export class Switch extends Component {}
  export class Switcher extends Component {}
  export class Tabs extends Component {}
  export class TabView extends Component {}
  export class Text extends Component {}
  export class Textfield extends Component {}
  export class Toolbar extends Component {}
  export class Tooltip extends Component {}
  export class View extends Component {}

  // Mixins
  export const EventEmitter: any;
  export const display: any;
  export const build: any;
  export const position: {
    position(target: HTMLElement, position?: any): any;
  };

  // Modules
  export const attributes: any;
  export const clone: any;
  export const create: any;
  export const dataset: any;
  export const device: any;
  export const emitter: any;
  export const events: any;
  export const jsonToHTML: any;
  export const mediator: any;
  export const merge: any;
  export const observer: any;
  export const request: any;
  export const ripple: any;
  export const smoothscroll: any;
  export function touch(): boolean;
}
